import React, { Component } from "react";
import Modal from "./components/Modal";
import axios from 'axios';  
import LoginForm from './LoginForm';
import RegisterForm from './RegisterForm';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      viewCompleted: false,
      activeItem: {
        title: "",
        description: "",
        completed: false
      },
      taskList: [],
      loggedIn: false,
      username: '',
    };
  }

  componentDidMount() {
    this.refreshList();
  }

  refreshList = () => {
    axios
      .get("http://localhost:8000/api/task/", {
        headers: {
          Authorization: `Token ${localStorage.getItem('token')}`
        }
      })
      .then(res => this.setState({ taskList: res.data }))
      .catch(err => console.log(err));
  };

  displayCompleted = status => {
    this.setState({ viewCompleted: status });
  };

  renderTabList = () => {
    return (
      <div className="my-5 tab-list">
        <span
          onClick={() => this.displayCompleted(true)}
          className={this.state.viewCompleted ? "active" : ""}
        >
          Completed
        </span>
        <span
          onClick={() => this.displayCompleted(false)}
          className={this.state.viewCompleted ? "" : "active"}
        >
          Incompleted
        </span>
      </div>
    );
  };

  renderItems = () => {
    const { viewCompleted } = this.state;
    const newItems = this.state.taskList.filter(
      item => item.completed === viewCompleted
    );
    return newItems.map(item => (
      <li
        key={item.id}
        className="list-group-item d-flex justify-content-between align-items-center"
      >
        <span
          className={`todo-title mr-2 ${viewCompleted ? "completed-todo" : ""}`}
          title={item.description}
        >
          {item.title}
        </span>
        <span>
          <button
            onClick={() => this.editItem(item)}
            className="btn btn-secondary mr-2"
          >
            Edit
          </button>
          <button
            onClick={() => this.handleDelete(item)}
            className="btn btn-danger"
          >
            Delete
          </button>
        </span>
      </li>
    ));
  };

  toggle = () => {
    this.setState({ modal: !this.state.modal });
  };

  handleSubmit = item => {
    this.toggle();
    if (item.id) {
      axios
        .put(`http://localhost:8000/api/task/${item.id}/`, item, {
          headers: {
            Authorization: `Token ${localStorage.getItem('token')}`
          }
        })
        .then(res => this.refreshList());
      return;
    }
    axios
      .post("http://localhost:8000/api/task/", item, {
        headers: {
          Authorization: `Token ${localStorage.getItem('token')}`
        }
      })
      .then(res => this.refreshList());
  };

  handleDelete = item => {
    axios
      .delete(`http://localhost:8000/api/task/${item.id}/`, {
        headers: {
          Authorization: `Token ${localStorage.getItem('token')}`
        }
      })
      .then(res => this.refreshList());
  };

  createItem = () => {
    const item = { title: "", description: "", completed: false };
    this.setState({ activeItem: item, modal: !this.state.modal });
  };

  editItem = item => {
    this.setState({ activeItem: item, modal: !this.state.modal });
  };

  // Function to handle user login
  handleLogin = (username, password) => {
    axios.post('http://localhost:8000/api/login/', { username, password })
      .then(res => {
        localStorage.setItem('token', res.data.token);
        localStorage.setItem('username', username);
        this.setState({ loggedIn: true, username });
        this.refreshList();
      })
      .catch(err => console.log(err));
  };

  // Function to handle user registration
  handleRegister = (username, password) => {
    axios.post('http://localhost:8000/api/register/', { username, password })
      .then(res => {
        this.handleLogin(username, password);
      })
      .catch(err => console.log(err));
  };

  // Function to handle user logout
  handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    this.setState({ loggedIn: false, username: '', taskList: [] });
  };

  render() {
    // If user is not logged in, render login form
    if (!this.state.loggedIn) {
      return (
        <div>
          <LoginForm onLogin={this.handleLogin} />
          <RegisterForm onRegister={this.handleRegister} />
        </div>
      );
    }

    // If user is logged in, render task manager
    return (
      <div>
        <header>
          <h1 className="text-black text-uppercase text-center my-4">Task Manager</h1>
          <button onClick={this.handleLogout} className="btn btn-secondary">Logout</button>
        </header>
        <main className="content main-content">
          <div className="row">
            <div className="col-md-6 col-sm-10 mx-auto p-0">
              <div className="card p-3">
                <div className="text-center mb-3">
                  <button onClick={this.createItem} className="btn btn-primary">
                    Add task
                  </button>
                </div>
                {this.renderTabList()}
                <ul className="list-group list-group-flush">
                  {this.renderItems()}
                </ul>
              </div>
            </div>
          </div>
          <footer className="my-3 mb-2 footer">
            Copyright 2024 &copy; All Right Reserved Rushikesh Patil 
          </footer>
        </main>
        {this.state.modal ? (
          <Modal
            activeItem={this.state.activeItem}
            toggle={this.toggle}
            onSave={this.handleSubmit}
          />
        ) : null}
      </div>
    );
  }
}

export default App;
